    private void btncifrarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btncifrarActionPerformed
        // TODO add your handling code here:
         String Texto = txtTexto.getText();
         String  Clave = txtclave.getText();
        if (Texto.length()>Clave.length()) {
            
            Vigikawaii obj = new Vigikawaii();
            String toUpperCase = Texto.toUpperCase();
            String toUpperCase1 = Clave.toUpperCase();
            System.out.println(toUpperCase+"--"+toUpperCase1);
            txtcifra.setText(obj.encriptarTextoClaro( toUpperCase , toUpperCase1 ));
            
        }else if(Texto ==  " " || Clave ==  " "){
            Regla2.setVisible(true);
            img.setVisible(false);
            img2.setVisible(false);
            kirby.setVisible(true);
        }else{
               regla1.setVisible(true);
               img.setVisible(false);
               img2.setVisible(true);
        
        }
     
          
        
        
        
    }//GEN-LAST:event_btncifrarActionPerformed

