    private void btndesActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btndesActionPerformed
        // TODO add your handling code here:
        String cifrado = txtcifra.getText();
          String clave = txtclave.getText();
        
        if (cifrado.length()>clave.length()) {
           Vigikawaii obj = new Vigikawaii();
        
        String mayu = cifrado.toUpperCase();
       
        String mayu2 = clave.toUpperCase();
        System.out.println(mayu +" "+mayu2);
        String nuevo = obj.desencriptarTextoClaro(mayu, mayu2);
            
        txtTexto.setText(nuevo);
        }else{
     
            Regla2.setVisible(true);
            img.setVisible(false);
            img2.setVisible(false);
            kirby.setVisible(true);
        }
        
        
        
        
        
    }//GEN-LAST:event_btndesActionPerformed

